import React from "react";
import { Grid } from "@material-ui/core";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import ReactGA from "react-ga";
import { BccTypography, BccButton } from "./BccComponents";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    [theme.breakpoints.down("sm")]: {
      root: {
        maxWidth: "100%",
        margin: "0 auto",
        marginTop: 85,
        padding: "0 16px 32px",
      },
      title: {
        marginBottom: 16,
        fontSize: 32,
        fontWeight: "bold",
        lineHeight: 1,
      },
      imgBlock: {
        gridArea: "a",
        width: "100%",
        position: "relative",
        "& > img": {
          position: "relative",
          width: "100%",
        },
      },
      textBlock: {
        width: "100%",
        padding: "32px 16px",
        gridArea: "b",
      },
      container: {
        backgroundColor: "#F1F7F9",
        borderRadius: 24,
        width: "100%",
        flexWrap: "wrap",
        display: "grid",
        gridTemplateAreas: '"b" "a"',
      },
    },
    [theme.breakpoints.between("md", "xl")]: {
      root: {
        maxWidth: 1280,
        margin: "0 auto",
        marginTop: 80,
        padding: "0 56px 80px",
      },
      title: {
        marginBottom: 16,
      },
      imgBlock: {
        width: "50%",
        position: "relative",
        "& > img": {
          position: "absolute",
          bottom: 0,
          left: 0,
        },
      },
      textBlock: { width: "50%", padding: "96px 64px 80px 0" },
      container: {
        backgroundColor: "#F1F7F9",
        borderRadius: 24,
        width: "100%",
      },
    },
  })
);

const PhoneCase1 = (props: any) => {
  const classes = useStyles({});
  return (
    <div className={classes.root}>
      <Grid
        container
        justify="space-between"
        wrap="nowrap"
        className={classes.container}
      >
        <Grid item className={classes.imgBlock}>
          <img src={process.env.PUBLIC_URL + "/phonecase1.svg"} alt="app" />
        </Grid>
        <Grid item className={classes.textBlock}>
          <BccTypography block type="h1" className={classes.title}>
            Финансовая грамотность
          </BccTypography>
          <BccTypography block type="p2l" className={classes.subTitle}>
            Ребенок научится тратить с умом и считать карманные деньги и быть
            более дисцеплинированным
          </BccTypography>
        </Grid>
      </Grid>
    </div>
  );
};

export default PhoneCase1;
