import React from "react";
import { Grid, Typography } from "@material-ui/core";
import {
  makeStyles,
  createStyles,
  Theme,
  useTheme,
} from "@material-ui/core/styles";
import { BccTypography, BccButton } from "./BccComponents";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Button from "@material-ui/core/Button";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import ReactGA from "react-ga";
import MaskedInput from "react-maskedinput";
import { useTranslation } from "react-i18next";
import BlockUi from "react-block-ui";
import { Snackbar } from "@material-ui/core";
import { Alert as MuiAlert } from "@material-ui/lab";
import "react-block-ui/style.css";

const webConfigEnv = (window as any).env;

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    [theme.breakpoints.down("md")]: {
      root: {
        alignItems: "center",
        margin: "16px auto",
        width: "90%",
      },
      icon: {
        width: "18px",
        height: "19px",
      },
      paper: {
        padding: "22px 16px 22px 16px",
        backgroundColor: "white",
        border: "1px solid #E8E8E8",
        boxSizing: "border-box",
        borderRadius: 8,
      },
      box: {
        textAlign: "center",
        fontWeight: "bold",
        fontSize: "20px",
        lineHeight: "20px",
      },
      formControlCheckBox: {
        marginTop: "20px",
      },
      checkBoxLabel: {
        fontStyle: "normal",
        fontWeight: "normal",
        fontSize: 12,
        color: "black",
      },
      submit: {
        background: "#3F0259",
        borderRadius: 4,
        fontSize: 16,
        fontWeight: 500,
        fontStyle: "normal",
        boxShadow: "none",
        textTransform: "none",
        height: 40,
        color: "#FFFFFF",
        "&:hover, &:active": {
          backgroundColor: "#3F0259",
          borderColor: "#3F0259",
          opacity: 0.8,
          boxShadow: "none",
          color: "#FFFFFF",
        },
        "&:disabled": {
          backgroundColor: "#3F0259",
          opacity: 0.4,
          boxShadow: "none",
          color: "#FFFFFF",
        },
      },
    },
    [theme.breakpoints.between("md", "xl")]: {
      root: {
        maxWidth: 1280,
        width: "70%",
        margin: "36px auto",
      },
      form: {
        maxWidth: 650,
        margin: "0 auto",
      },
      paper: {
        padding: "45px 72px 45px 72px",
        background: "#FFFFFF",
        border: "2px solid #FAFAFA",
        boxSizing: "border-box",
        borderRadius: "8px",
      },
      icon: {
        width: "18px",
        height: "19px",
      },
      box: {
        textAlign: "center",
        fontWeight: "bold",
        fontSize: "40px",
        lineHeight: "40px",
        marginBottom: "27px",
      },
      formControlCheckBox: {
        marginTop: "25px",
      },
      checkBoxLabel: {
        fontStyle: "normal",
        fontWeight: "normal",
        fontSize: 16,
        color: "black",
      },
      submit: {
        background: "#3F0259",
        borderRadius: "8px",
        fontSize: "20px",
        lineHeight: "28px",
        fontWeight: "500",
        fontStyle: "normal",
        textTransform: "none",
        boxShadow: "none",
        height: "62px",
        color: "#FFFFFF",
        "&:hover, &:active": {
          backgroundColor: "#3F0259",
          borderColor: "#3F0259",
          opacity: 0.8,
          boxShadow: "none",
          color: "#FFFFFF",
        },
        "&:disabled": {
          backgroundColor: "#3F0259",
          opacity: 0.6,
          boxShadow: "none",
          color: "#FFFFFF",
        },
      },
    },
    timer: {
      fontSize: 16,
      color: "#4D565F",
    },
    linkReSendSms: {
      color: "#3F0259",
      fontSize: 16,
      cursor: "pointer",
      "&:hover, &:active": {
        textDecoration: "underline",
        opacity: 0.8,
      },
    },
    code: {
      margin: 0,
      "& input": {
        height: 62,
        boxSizing: "border-box",
      },
    },
    successForm: {
      padding: "30px",
      marginTop: 64,
      borderRadius: 8,
      backgroundColor: "rgba(125, 206, 160, 0.2)",
      textAlign: "center",
      "& > img": {
        display: "block",
        margin: "0 auto",
        marginBottom: 23,
      },
      "& > div": {
        display: "block",
        fontSize: 18,
        fontWeight: "bold",
        marginBottom: 16,
        color: "#1F7042",
      },
      "& > span": {
        display: "block",
        fontSize: 16,
        color: "#1F7042",
        marginBottom: 60,
      },
    },
    warningForm: {
      backgroundColor: "rgba(200, 79, 79, 0.05)",
      "& > div": {
        color: "#000D1A",
        fontSize: 16,
        fontWeight: "normal",
        marginBottom: 60,
      },
    },
    starbankingForm: {
      backgroundColor: "#FAFAFA",
      padding: "56px 30px 0",
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-between",
      flexWrap: "nowrap",
      "& > div": {
        marginBottom: 0,
      },
    },
    linkBlock: {
      width: "70%",
      alignSelf: "center",
      textAlign: "left",
    },
    starText: {
      fontSize: 12,
      color: "#000D1A",
      padding: "0 10px 0 0",
    },
    starQr: {
      height: "48px!important",
      marginBottom: 12,
    },
    starImages: {
      marginTop: 24,
      "& > img": {
        height: 32,
        verticalAlign: "middle",
        marginRight: 16,
        marginBottom: 12,
        cursor: "pointer",
      },
    },
    imgBlock: {
      width: "30%",
      "& > img": {
        width: "100%",
      },
    },
    hintText: {
      fontSize: 14,
      textAlign: "center",
      color: "#141414",
      opacity: 0.7,
    },
  })
);

const Alert = (props: any) => {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
};

interface TextMaskCustomProps {
  inputRef: (ref: HTMLInputElement | null) => void;
}

const TextMaskCustom = (props: TextMaskCustomProps) => {
  const { inputRef, ...other } = props;

  return (
    <MaskedInput
      {...other}
      ref={(ref: any) => inputRef(ref ? ref.inputElement : null)}
      mask="7(111) 111 11 11"
      placeholder={"7(707) 707 77 77"}
    />
  );
};

const CardOrder = (props: any) => {
  const [fio, setFio] = React.useState("");
  const [phoneNumber, setPhoneNumber] = React.useState("");
  const [agree, setAgree] = React.useState<boolean>(true);
  const [phoneError, setPhoneError] = React.useState<boolean>(false);
  const [isLoading, setLoading] = React.useState(false);
  const [openError, setOpenError] = React.useState(false);
  const classes = useStyles({});
  const { t } = useTranslation();
  const theme = useTheme();
  const isXS = useMediaQuery(theme.breakpoints.down("sm"));

  const isValid = () => {
    return (
      fio.length > 1 && phoneNumber.replace("_", "").length === 16 && agree
    );
  };

  const formatPhoneNumber = () => {
    let res = phoneNumber;
    if (phoneNumber.slice(0, 1) === "8") res = "7" + phoneNumber.slice(1);
    return res.replace(/\(|\)| /g, "");
  };

  const onClickAppStore = () => {
    ReactGA.event({
      category: "BccCard_AppStore_download",
      action: "AppStore_download",
    });

    window.open(
      "https://apps.apple.com/kz/app/starbanking/id743617904",
      "_blank"
    );
  };

  const onClickGooglePlay = () => {
    ReactGA.event({
      category: "BccCard_GooglePlay_download",
      action: "GooglePlay_download",
    });

    window.open(
      "https://play.google.com/store/apps/details?id=kz.bcc.starbanking&hl=ru",
      "_blank"
    );
  };

  const handleClose = () => {
    setOpenError(false);
  };

  return (
    <Grid
      ref={props.refProp}
      id="order"
      container
      className={classes.root}
      spacing={4}
      direction="column"
      justify="center"
    >
      <Paper elevation={0} className={classes.paper}>
        <Typography className={classes.box}>
          Заполните заявку и получите
          <br />
          Junior Card
        </Typography>
        <Snackbar
          anchorOrigin={{
            vertical: "top",
            horizontal: "center",
          }}
          open={openError}
          autoHideDuration={6000}
          onClose={handleClose}
        >
          <Alert onClose={handleClose} severity="error">
            Возникла непредвиденная ошибка!
          </Alert>
        </Snackbar>
        <div className={classes.form}>
          <BlockUi tag="div" blocking={isLoading}>
            <>
              <TextField
                size={isXS ? "small" : "medium"}
                variant="outlined"
                margin="normal"
                fullWidth
                id="iin"
                label="ФИО"
                name="fio"
                value={fio}
                onChange={(e: any) => setFio(e.target.value.replace(/\D/g, ""))}
              />
              <TextField
                size={isXS ? "small" : "medium"}
                variant="outlined"
                margin="normal"
                fullWidth
                id="phone"
                name="phone"
                helperText={phoneError ? "Неверный формат номера телефона" : ""}
                error={phoneError ? true : false}
                value={phoneNumber}
                onChange={(e: any) => setPhoneNumber(e.target.value)}
                label="Номер телефона"
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  inputComponent: TextMaskCustom as any,
                }}
              />
              <FormControlLabel
                className={classes.formControlCheckBox}
                control={
                  <Checkbox
                    value="remember"
                    color="primary"
                    checked={agree}
                    onChange={() => setAgree(!agree)}
                  />
                }
                label={
                  <Typography className={classes.checkBoxLabel}>
                    Я согласен(а) на сбор и обработку персональных данных
                  </Typography>
                }
              />
              <Grid
                container
                style={{ marginTop: "16px", marginBottom: "16px" }}
                spacing={2}
              >
                <Grid
                  item
                  xl={false}
                  lg={false}
                  md={false}
                  sm={false}
                  xs={false}
                >
                  <img
                    src="card_order_security.svg"
                    className={classes.icon}
                    alt="order_security"
                  />
                </Grid>
                <Grid item xl={true} lg={true} md={true} sm={true} xs={true}>
                  <BccTypography type="p3" block>
                    Мы гарантируем безопасность и сохранность ваших данных
                  </BccTypography>
                </Grid>
                <BccButton
                  onClick={() => {}}
                  fullWidth
                  variant="contained"
                  className={classes.submit}
                  disabled={!isValid()}
                >
                  {t("block_6.next_main")}
                </BccButton>
              </Grid>
            </>
          </BlockUi>
        </div>
      </Paper>
    </Grid>
  );
};

export default CardOrder;
